import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

import About from './components/About'; // Corrected import path
import Countdown from './components/Countdown'; // Corrected import path
import NewsletterModal from './components/NewsletterModal'; // Corrected import path

function App() {
  const [showModal, setShowModal] = useState(false);

  // Calculate the date 30 days from now
  const thirtyDaysFromNow = new Date();
  thirtyDaysFromNow.setDate(thirtyDaysFromNow.getDate() + 30);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowModal(true);
    }, thirtyDaysFromNow - Date.now());

    return () => clearTimeout(timer);
  }, [thirtyDaysFromNow]);

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path="/" element={<About />} />
          <Route path="/about" element={<About />} />
        </Routes>
      </Router>

      {/* Display the Countdown component */}
      <Countdown endDate={thirtyDaysFromNow} />

      {/* Display the NewsletterModal component */}
      <NewsletterModal onClose={() => setShowModal(false)} isVisible={showModal} />
    </div>
  );
}

export default App;
