import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import About from './components/About';
import Countdown from './components/Countdown';
import NewsletterModal from './components/NewsletterModal';

function App() {
  const [showModal, setShowModal] = useState(false);

  // Calculate the date 30 days from now
  const thirtyDaysFromNow = new Date();
  thirtyDaysFromNow.setDate(thirtyDaysFromNow.getDate() + 30);

  useEffect(() => {
    // Set a timeout for 30 days
    const timer = setTimeout(() => {
      setShowModal(true);
    }, thirtyDaysFromNow - Date.now());

    return () => clearTimeout(timer);
  }, []);

  return (
    <Router>
      <div className="App">
        <h1>My Resume Website</h1>
        <p>Countdown Timer:</p>
        <Countdown endDate={thirtyDaysFromNow} />
        <p>I will be writing blogs here once the timer ends!</p>
        <Routes>
          <Route path="/" element={<About />} />
          <Route path="/about" element={<About />} />
        </Routes>
        <NewsletterModal onClose={() => setShowModal(false)} isVisible={showModal} />
      </div>
    </Router>
  );
}

export default App;
