import React, { useEffect, useState, useMemo } from 'react';
import './App.css';

import Countdown from './Countdown';

function App() {
  // Calculate the date 30 days from now
  const thirtyDaysFromNow = useMemo(() => {
    const endDate = new Date();
    endDate.setDate(endDate.getDate() + 30);
    return endDate;
  }, []);

  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowModal(true);
    }, thirtyDaysFromNow - Date.now());

    return () => clearTimeout(timer);
  }, [thirtyDaysFromNow]);

  return (
    <div className="App">
      {/* Display the Countdown component */}
      <Countdown endDate={thirtyDaysFromNow} />

      {/* Display the NewsletterModal component */}
      {/* You can add this back if needed */}
      {/* <NewsletterModal onClose={() => setShowModal(false)} isVisible={showModal} /> */}
    </div>
  );
}

export default App;
