import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

import Countdown from './Countdown'; // Corrected import path

function App() {
  const [showModal, setShowModal] = useState(false);

  // Calculate the date 30 days from now
  const thirtyDaysFromNow = new Date();
  thirtyDaysFromNow.setDate(thirtyDaysFromNow.getDate() + 30);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowModal(true);
    }, thirtyDaysFromNow - Date.now());

    return () => clearTimeout(timer);
  }, [thirtyDaysFromNow]);

  return (
    <div className="App">
      <Router>
        <Routes>
          {/* Define your routes here */}
        </Routes>
      </Router>

      {/* Display the Countdown component */}
      <Countdown endDate={thirtyDaysFromNow} />

      {/* Display the NewsletterModal component */}
      {/* You can add this back if needed */}
      {/* <NewsletterModal onClose={() => setShowModal(false)} isVisible={showModal} /> */}
    </div>
  );
}

export default App;
